import React, { useState,useEffect,useCallback} from "react";
import chipImage from "./Images/chip.png";
import visa from "./Images/visa.png"
import './App.css';

function App() {
  const [number,setNumber]= useState('');
  const [displaynum, setDisplaynum]= useState('#### #### #### ####')
  const [name,setName]= useState('');
  const [month,setMonth]= useState('');
  const [year,setYear]= useState('');
  const [dismonth,setDMonth]= useState('MM');
  const [disyear,setDYear]= useState('YY');
  const [cvv,setCvv] = useState('');
  const [focus,setFocus]= useState('');
  function handleCvvOver(e){
    document.getElementsByClassName("flipcardinner")[0].style.transform = "rotateY(180deg)"
  }
  function handleCvvOut(e){
    document.getElementsByClassName("flipcardinner")[0].style.transform = ""
  }
  function handleCardNumChange(e){
    setNumber(e.target.value);
    var x = e.target.value;
    var l = 16-x.length;
  for(let i=0; i <l ;i++){
    x = x+ "#"
   }
  var k =  x.split('');
  for(let i=4;i<=16;i=i+4){
    k.splice(i,0,' ')
    i=i+1;
  }
  x = k.join('');
    setDisplaynum(x)
  }  
  return (

    <div className="App">

<div className ="flipcard">
  <div className ="flipcardinner">
 <div className="creditcardfront" >
 <img src= {chipImage} style={{"height":"40px",'marginRight':'75%'}}></img>
 <img src= {visa} style={{"height":"35px", 'marginLeft':'65%'}}></img>
 <br></br>
 <span style={{'color':'white','fontSize':'25px'}}>{displaynum}</span>
 <br></br>
 <div style={{'textAlign':'left','marginLeft':'15px'}}>
<label style={{'color':"#f0f0f0"}}>Card Holder</label> 
<br></br>
   <span style={{'color':'white','textTransform':'uppercase'}}>{name}</span>
   <br></br>
   </div>
   <div style={{'textAlign':'right','marginRight':'20px','marginBottom':'-10px'}}>
     <label style={{'color':"#f0f0f0"}}>Expires</label>
     <br></br>
   <span style={{'color':'white'}}>{dismonth}/</span><span style={{'color':'white'}}>{disyear}</span>
   </div>

 </div>
 <div className="creditcardback">
  <h2 style={{'backgroundColor':'#000000','width':'100%','color':'black'}}>..</h2>
  <label style={{'textAlign':'right','marginLeft':'75%','color':'white','fontSize':'medium'}} >CVV</label>
 <p style={{'backgroundColor':'#ffffff','width':'95%','color':'black','borderRadius':'6px','marginLeft':'10px','height':'40px','marginTop':'1px','textAlign':'right'}}>{cvv}</p>
 <div>
 <img src= {visa} style={{"height":"35px",'marginLeft':'65%'}}></img>
 </div>
 </div>
 </div>
</div>
 <div className = "carddetails">
   <div className ="carddetailsinner" style={{'marginTop':'1cm'}}>
 <label style={{'marginRight':'83%'}}>Card Number</label>
 <br></br>
          <input
          style={{'width':'100%'}}
          id="cardnumber"
            type="tel"
            name="number"
            value = {number} 
            onChange = {handleCardNumChange}
            maxLength="16"
            pattern="^[0-9]*$"
          />
        <br></br>
        <label style={{'marginRight':'86%'}}>Card Name</label>
        <br></br>
          <input
            style={{'width':'100%'}}
          id ="cardname"
            type="text"
            name="name"

            value = {name}
            onChange = {e=> setName(e.target.value)}
          />
           <br></br>
           <label style={{'marginRight':'77%'}}>Expiration Date</label>
           <label>Cvv</label>
           <br></br>
          <input
          style={{'width':'110px'}}
          id = "month"
            type="number"
            name="number"
            placeholder="Month"
            min = "1"
            max = "12"
            value = {month}
            onChange = {e=> setMonth(e.target.value)}
          />

          <input
          style={{'width':'110px'}}
          id = "year"
            type="number"
            name="number"
            placeholder="Year"
            min = "2021"
            max ="2040"
            value = {year}
            onChange = {e=> setYear(e.target.value)}

          />

          <input
          style={{'width':'237px'}}
          id = "cvv"
            type="tel"
            name="number"
            value = {cvv}
            onChange = {e=> setCvv(e.target.value)}
            onMouseOver = {handleCvvOver}
            onMouseOut = {handleCvvOut}
          />
          <br></br>
          <button>Submit</button>
          </div>
 </div>
</div>
  );
}

export default App;
