import React, { useState,useEffect,useCallback} from "react";
import chipImage from "./Images/chip.png";
import visa from "./Images/visa.png"
import './App.css';

function App() {
  const [number,setNumber]= useState('');
  const [name,setName]= useState('');
  const [month,setMonth]= useState('');
  const [year,setYear]= useState('');
  const [cvv,setCvv] = useState('');
  const [focus,setFocus]= useState('');
  function handleCvvOver(e){
    document.getElementsByClassName("flipcardinner")[0].style.transform = "rotateY(180deg)"
  }
  function handleCvvOut(e){
    document.getElementsByClassName("flipcardinner")[0].style.transform = ""
  }
  return (

    <div className="App">

<div className ="flipcard">
  <div className ="flipcardinner">
 <div className="creditcardfront" >
 <img src= {chipImage} style={{"height":"40px", 'marginTop':'15px','marginRight':'75%'}}></img>
 <img src= {visa} style={{"height":"35px", 'marginLeft':'65%'}}></img>
 <span style={{'color':'white'}}>{number}</span>
 <br></br>
   <span style={{'color':'white'}}>{name}</span>
   <br></br>
   <span style={{'color':'white'}}>{month}/</span><span style={{'color':'white'}}>{year}</span>


 </div>
 <div className="creditcardback">
  <h2 style={{'backgroundColor':'#000000','width':'100%','color':'black'}}>..</h2>
  <label style={{'textAlign':'right','marginLeft':'75%','color':'white','fontSize':'medium'}} >CVV</label>
 <h3 style={{'backgroundColor':'#ffffff','width':'90%','color':'black','borderRadius':'8px','marginLeft':'5px'}}>{cvv}</h3>
 <div>
 <img src= {visa} style={{"height":"35px", 'marginTop':'10%','marginLeft':'65%'}}></img>
 </div>
 </div>
 </div>
</div>
 <div className = "carddetails">
   <div className ="carddetailsinner" style={{'marginTop':'1cm'}}>
 <label style={{'marginRight':'83%'}}>Card Number</label>
 <br></br>
          <input
          style={{'width':'100%'}}
          id="cardnumber"
            type="tel"
            name="number"
            value = {number} 
            onChange = {e=> setNumber(e.target.value)}
          />
        <br></br>
        <label style={{'marginRight':'86%'}}>Card Name</label>
        <br></br>
          <input
            style={{'width':'100%'}}
          id ="cardname"
            type="text"
            name="name"

            value = {name}
            onChange = {e=> setName(e.target.value)}
          />
           <br></br>
           <label style={{'marginRight':'77%'}}>Expiration Date</label>
           <label>Cvv</label>
           <br></br>
          <input
          style={{'width':'110px'}}
          id = "month"
            type="number"
            name="number"
            placeholder="Month"
            min = "1"
            max = "12"
            value = {month}
            onChange = {e=> setMonth(e.target.value)}
          />

          <input
          style={{'width':'110px'}}
          id = "year"
            type="number"
            name="number"
            placeholder="Year"
            min = "2021"
            max ="2040"
            value = {year}
            onChange = {e=> setYear(e.target.value)}

          />

          <input
          style={{'width':'237px'}}
          id = "cvv"
            type="tel"
            name="number"
            value = {cvv}
            onChange = {e=> setCvv(e.target.value)}
            onMouseOver = {handleCvvOver}
            onMouseOut = {handleCvvOut}
          />
          <br></br>
          <button>Submit</button>
          </div>
 </div>
</div>
  );
}

export default App;
