import React, { useState,useEffect,useCallback} from "react";
import chipImage from "./Images/chip.png";
import visa from "./Images/visa.png"
import mastercard from "./Images/mastercard.png"
import './App.css';

function App() {
  const [number,setNumber]= useState('');
  const [displaynum, setDisplaynum]= useState('#### #### #### ####')
  const [name,setName]= useState('');
  const [month,setMonth]= useState('');
  const [year,setYear]= useState('');
  const [dismonth,setDMonth]= useState('MM');
  const [disyear,setDYear]= useState('YYYY');
  const [cvv,setCvv] = useState('');
  const [img, setImage]= useState(visa)
  const [focus,setFocus]= useState('');
  function getCardType(cardNum) {

    var payCardType = "";
    var regexMap = [
      {regEx: /^4[0-9]{5}/ig,cardType: "VISA"},
      {regEx: /^5[1-5][0-9]{4}/ig,cardType: "MASTERCARD"},
      {regEx: /^(?:6011\d{12})|(?:65\d{14})/ig, cardType: "DISCOVER"},
      {regEx: /^3[47][0-9]{3}/ig,cardType: "AMEX"},
      {regEx: /^(5[06-8]\d{4}|6\d{5})/ig,cardType: "MAESTRO"}
    ];

    for (var j = 0; j < regexMap.length; j++) {
      if (cardNum.match(regexMap[j].regEx)) {
        payCardType = regexMap[j].cardType;
        break;
      }
    }

    if (cardNum.indexOf("50") === 0 || cardNum.indexOf("60") === 0 || cardNum.indexOf("65") === 0) {
      var g = "508500-508999|606985-607984|608001-608500|652150-653149";
      var i = g.split("|");
      for (var d = 0; d < i.length; d++) {
        var c = parseInt(i[d].split("-")[0], 10);
        var f = parseInt(i[d].split("-")[1], 10);
        if ((cardNum.substr(0, 6) >= c && cardNum.substr(0, 6) <= f) && cardNum.length >= 6) {
         payCardType = "RUPAY";
          break;
        }
      }
    }
    return payCardType;

}
  function handleCvvOver(e){
    document.getElementsByClassName("flipcardinner")[0].style.transform = "rotateY(180deg)"
  }
  function handleCvvOut(e){
    document.getElementsByClassName("flipcardinner")[0].style.transform = ""
  }
  function handleMonthChange(e){
    setMonth(e.target.value);
    var m = e.target.value;
    setDMonth(m);
  }
  function handleYearChange(e){
    setYear(e.target.value);
    var y = e.target.value;
    setDYear(y);
  }
  function handleCardNumChange(e){
    e.preventDefault();
    setNumber(e.target.value);
    var x = e.target.value;
   var card_type = getCardType(x);
   console.log(card_type)
    var l = 16-x.length;
  for(let i=0; i <l ;i++){
    x = x+ "#"
   }
  var k =  x.split('');
  for(let i=4;i<=16;i=i+4){
    k.splice(i,0,' ')
    i=i+1;
  }
  x = k.join('');
    setDisplaynum(x)
  }  
  return (

    <div className="App">

  <div className ="flipcard">
    <div className ="flipcardinner">
      <div className="creditcardfront" >
        <span style={{"height":"40px",'marginRight':'75%' , 'marginTop':'15px'}}>
        <img src= {chipImage} style={{"height":"40px", 'marginTop':'15px'}} ></img>
        </span>
          <span style={{"height":"35px", 'marginLeft':'65%', 'display':'inline-block'}}>
          <img src= {img} style={{"height":"inherit",  'display':'inherit' ,'marginTop':'-30px'}}></img>
          </span>
           <br></br>
            <div style={{'color':'#f0f0f0','fontSize':'23px','fontFamily':'courier'}}><label style={{'fontSize':'larger','border':'1px solid #eeeeee'}}>{displaynum}</label></div>
              <br></br>
      <div style={{'textAlign':'left','marginLeft':'15px'}}>
          <label style={{'color':" rgb(141, 138, 138)",'fontFamily':'FreeMono, monospace'}}>Card Holder</label> 
              <br></br>
   <span style={{'color':'white','textTransform':'uppercase'}}>{name}</span>
   <br></br>
   </div>
   <div style={{'textAlign':'right','marginRight':'20px','marginTop':'-40px'}}>
     <label style={{'color':" rgb(141, 138, 138)",'fontFamily':'FreeMono, monospace'}}>Expires</label>
     <br></br>
   <span style={{'color':'white'}}>{dismonth}/</span><span style={{'color':'white'}}>{disyear}</span>
   </div>

 </div>
 <div className="creditcardback">
  <h2 style={{'backgroundColor':'#000000','width':'100%','color':'black'}}>..</h2>
  <label style={{'textAlign':'right','marginLeft':'75%','color':'white','fontSize':'medium'}} >CVV</label>
 <p style={{'backgroundColor':'#ffffff','width':'95%','color':'black','borderRadius':'6px','marginLeft':'10px','height':'40px','marginTop':'1px','textAlign':'right'}}>{cvv}</p>
 <div>
 <img src= {visa} style={{"height":"35px",'marginLeft':'65%'}}></img>
 </div>
 </div>
 </div>
</div>
 <div className = "carddetails">
   <div className ="carddetailsinner" style={{'marginTop':'1cm'}}>
 <label style={{'marginRight':'83%'}}>Card Number</label>
 <br></br>
          <input
          style={{'width':'100%'}}
          id="cardnumber"
            type="text"
            name="number"
            value = {number} 
            onChange = {handleCardNumChange}
            maxlength = "16"
            inputm

          />
        <br></br>
        <label style={{'marginRight':'86%'}}>Card Name</label>
        <br></br>
          <input
            style={{'width':'100%'}}
          id ="cardname"
            type="text"
            name="name"

            value = {name}
            onChange = {e=> setName(e.target.value)}
          />
           <br></br>
           <label style={{'marginRight':'77%'}}>Expiration Date</label>
           <label>Cvv</label>
           <br></br>
          <input
          style={{'width':'110px'}}
          id = "month"
            type="number"
            name="number"
            placeholder="Month"
            min = "1"
            max = "12"
            value = {month}
            onChange = {handleMonthChange}
          />

          <input
          style={{'width':'110px'}}
          id = "year"
            type="number"
            name="number"
            placeholder="Year"
            min = "2021"
            max ="2040"
            value = {year}
            onChange = {handleYearChange}

          />

          <input
          style={{'width':'237px'}}
          id = "cvv"
            type="tel"
            name="number"
            value = {cvv}
            onChange = {e=> setCvv(e.target.value)}
            onMouseOver = {handleCvvOver}
            onMouseOut = {handleCvvOut}
          />
          <br></br>
          <button>Submit</button>
          </div>
 </div>
</div>
  );
}

export default App;
